        cervejas.add(Cerveja ("0193","Ashby Porter","Após a invenção do malte tostado, esse tipo de cerveja foi extinto, ressurgindo apenas em 1980 com o nome Porter: uma homenagem aos trabalhadores da região portuária da Inglaterra, grandes fãs da bebida. Com traços suaves de chocolate, a Porter Ashby tem um equilíbrio que tende para o aroma e o sabor marcante.","Cervejaria Ashby","0030","4.8%","","8-12 ºC","#1E0B07","0006"))
